<script>
export default {
  name: "Navbar",
  methods: {
    navigateTo(page) {
      if (this.$route.path != page) this.$router.push(page);
    },
    animateNavbar() {
      let currentPage = this.$route.name;
      let activeNav = document.getElementById("nav-" + currentPage);
      let paths = activeNav.querySelectorAll(".path");
      let i = 0;

      paths.forEach(path => {
        i++;
        setTimeout(() => {
          path.style.stroke = "url(#linear)";
          path.classList.add(
            currentPage == "Donate"
              ? "animation-short"
              : currentPage == "Newsfeed"
              ? "animation-long"
              : "animation"
          );
        }, 30 * i);
      });
    }
  },
  mounted() {
    this.animateNavbar();
  }
};
</script>

